    function streamHandler(stream, isMuted) {
        switch (stream.type) {
            case "audio":
                VideoLayout.changeLocalAudio(stream, isMuted);
                break;
            case "video":
                VideoLayout.changeLocalVideo(stream, isMuted);
                break;
            case "stream":
                VideoLayout.changeLocalStream(stream, isMuted);
                break;
        }
    }
    
